code.css:
.section-container {
  /* Defined in Site CSS */
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-top: 1rem;
  padding-bottom: 1rem;
}

.logo {
  font-size: 1.5rem;
  font-weight: 600;
  line-height: 1.2;
  display: flex;
  gap: 10px;

  img {
    width: 30px;
  }

}

.desktop-nav {
  display: none;
  align-items: center;
  justify-content: space-between;
  width: 100%;

  nav {
    display: flex;
    gap: 2rem;

    a.link {
      /* Defined in Site CSS */
      border-color: transparent;
      transition: border-color 0.1s;
      font-size: 0.95rem;

      &:hover {
        border-color: initial;
      }
    }
  }

  @media (min-width: 800px) {
    display: flex;
  }
}

.mobile-nav {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  font-weight: 500;

  @media (min-width: 800px) {
    display: none;
  }
}

/* Mobile navigation popup */
#popup {
  font-size: 1.25rem;
  display: grid;
  gap: 1rem;
  position: absolute;
  text-align: center;
  background-color: var(--background-color, white);
  box-shadow: var(--box-shadow, 0px 4px 30px rgba(0, 0, 0, 0.1));
  border-radius: var(--border-radius);
  inset: 0.5rem;
  bottom: initial;
  padding: 2rem;
  z-index: 1;

  button#close {
    position: absolute;
    right: 1rem;
    top: 1rem;
  }
}

.section-container {
  max-width: var(--inner-max-width, 900px);
}

h2 {
  color: var(--color-heading);
  font-size: 1rem;
  line-height: 1.1;
  font-weight: 600;
  margin-bottom: 4rem;
  letter-spacing: -0.02rem;
  padding: .5rem 0;
  border-bottom: 1px solid #f0f0f0;
}

ul.items {
  display: grid;
  gap: 4rem;
}

li {
  display: grid;
  gap: 2rem;

  .post-info {
    .title {
      color: var(--color-heading);
      display: block;
      font-size: 1.3rem;
      font-weight: 600;
      margin-bottom: 0.75rem;
      line-height: 1.2;
      letter-spacing: -0.02rem;
      text-decoration: underline transparent;
      transition: 0.1s text-decoration;

      &:hover {
        text-decoration-color: initial;
      }
    }

    .description {
      font-size: 1rem;
      margin-bottom: 0.5rem;
      color: #2d3339;
    }

    .date {
      font-size: 1rem;
      font-weight: 400;
      color: var(--color-accent2);
      font-weight: 400;
    }
  }

  img {
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

@media (min-width: 600px) {
  li {
    grid-template-columns: 3fr 1fr;
    gap: 3rem;
  }
}

section {
  display: grid;
  place-items: center;
  max-width: var(--inner-max-width, 900px);
  padding-bottom: 3rem;
}

.heading {
  text-align: center;
  text-wrap: balance;
  margin-bottom: 3rem;
}

figure {
  display: grid;
  gap: 0.25rem;
  margin-bottom: 2.5rem;
  img {
    width: 100px;
    height: 100px;
    object-fit: cover;
    border-radius: 100%;
  }
  figcaption {
    color: var(--color-shade, #2d3339);
    font-size: 0.875rem;
    font-weight: 400;
    text-align: center;

  }
}
.content {
    line-height: 1.5;
    font-family: var(--body-font);
    color: #2d3339;

  :global(p){
    margin-bottom: 1rem;
  }

}

.section-container { /* Defined in Site CSS */
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  padding-top: 1rem;
  padding-bottom: 1rem;

  @media (min-width: 600px) {
    flex-direction: row;
  }
}

nav {
  display: flex;
  gap: 1rem;
  font-size:.875rem;

  .link { /* Defined in Site CSS */
    border-color: transparent;

    &:hover {
      border-color: initial;
    }
  }
}

.primo {
  font-weight: 600;
  font-size: 0.75rem;

  a {
    text-decoration: underline; 
  }
}

.social-links {
  display: flex;
  gap: 1.5rem;
  font-size: 1.5rem;

  a {
    color: black;
    transition: var(--transition, 0.1s transform);

    &:hover {
      transform: scale(1.1);
    }
  }
}

section {
  display: grid;

  max-width: var(--inner-max-width, 900px);
  padding-bottom: 3rem;
}

.content {
  gap: 1rem;

  .heading {
    margin-bottom: 10px;
  }

  .body{
    margin-bottom: .5rem;
  }
  .social-links {
    display: grid;
    gap: 0.75rem;

    a {
      display: flex;
      align-items: center;
      gap: 0.75rem;

      .icon {
        font-size: 1.75rem;
        color: black;
        transition: var(--transition, 0.1s transform);
      }

      &:hover .icon {
        transform: scale(1.1);
      }
    }
  }
}

form {
  display: grid;
  gap: 1rem;
  padding: 2rem;
  border: 1px solid var(--border-color, #eee);
  border-radius: var(--border-radius);

  label {
    display: grid;
    gap: 0.25rem;

    span {
      font-size: 0.75rem;
      font-weight: 500;
    }

    input,
    textarea {
      font-size: 1rem;
      padding: 0.5rem;
      outline-color: transparent;
      transition: var(--transition, 0.1s outline-color);
      border: 1.5px solid var(--border-color, #eee);
      border-radius: 4px;

      &:focus {
        outline-color: var(--color-accent, rebeccapurple);
      }
    }
  }

  .button { /* Defined in Site CSS */
    margin-top: 1rem;
    color: black;
  }
}
